var id = msg.payload.split(":")[0];
locations[id] = {
    "lat": Number(msg.payload.split(":")[1].split(",")[0]),
    "lon": Number(msg.payload.split(":")[1].split(",")[1]),
    "ts": Date.now()
}
global.set("locations",locations);
return msg;

var locations = global.get("locations") || {};
msg.payload.location = locations[msg.payload.id] || {"lat":0,"lon":0,"ts":0};
return msg;

var id = msg.payload.id;
var loc = msg.payload.location;
var txtlat = loc.lat.toFixed(4);
var txtlon = loc.lon.toFixed(4);

var response = 
"<html>" +
"<body>" +

"<center><h1>" + id + "</h1></center>" +

"<hr>" +
"<pre>" + 
Date(loc.ts) + "\n\n" +
"Latitude:  " + txtlat + "\n" +
"Longitude: " + txtlon + "\n" +
"</pre>" + 
"<hr>" +

'<iframe width="100%" height="800px" min-height="600px" frameborder="0" allowfullscreen ' + 
'src="http://umap.openstreetmap.fr/en/map/geoloc_239789#19/' + 
txtlat + '/' + txtlon + '?scaleControl=false&miniMap=false&scrollWheelZoom=false&zoomControl=true&allowEdit=false&moreControl=true&searchControl=null&tilelayersControl=null&embedControl=null&datalayersControl=true&onLoadPanel=undefined&captionBar=false">' +
'</iframe>' +
'<p><a href="http://umap.openstreetmap.fr/en/map/geoloc_239789#19/' + txtlat + '/' + txtlon + '">See full screen</a></p>' +

"</body>" +
"</html>" ;

msg.payload = response;
return msg;
